insert into products(name, price, description, stock, scent_id, brand_id) values('데 로스 산토스', 360000, '데 로스 산토스는 추억을 불러일으키고 덧없음을 인정하며, 삶을 축복하기 위해 만들어진 향기입니다. 삶을 기리는 멕시코의 죽은 자들의 날(Dia de los Muertos)과 올 세인츠 데이(A Sainis Day)의 전통에 경의를 표하며, 집단 의식의 초월적인 힘을 포착, 향기를 통해 삶을 찬미하는 의미를 담았습니다. 바이레도의 첫번째 아로마틱 프래그런스로 탑 노트는 신선한 향의 세이지와 미라벨이 조화를 이루고, 머스크에 중독성있는 프루티 노트를 더해 깊은 여운을 남깁니다.', 90, 2, 3);
insert into products(name, price, description, stock, scent_id, brand_id) values('라 튤립', 360000, '봄의 부활을 상징하는 튤립에서 영감을 받은 향으로 한 계절에 처음 맺는 꽃 봉오리처럼 활기넘치고 매력적이고 낙천적인 느낌을 담은 향기입니다.신선한 숲 향의 탑 노트로 시작되어 갓 체취된 꽃들의 줄기를 연상케합니다. 하트 노트인 튤립은 탑 노트를 관통하여 살짝의 상쾌함을 더하고, 베티버는 숲의 향을, 그리고 블론드 우드는 부드러운 우디향을 더합니다.', 90, 1, 3);
insert into products(name, price, description, stock, scent_id, brand_id) values('로즈 느와', 360000, '로즈느와는 전통적인 향기를 가진 다마스카스 장미의 향을 다크하게 변주한 향수입니다. 사랑과 헌신의 고전적인 상징인 장미가 로즈느와에서는 거칠고 퇴폐적이고, 관능적인 느낌으로 다가옵니다.자몽에서 온 시큼한 탑 노트로 시작되어, 곧바로 클래식한 다마스크가 꽃을 피우다 모스와 머스크의 다크한 흙 향으로 진화합니다', 90, 5, 3);
insert into products(name, price, description, stock, scent_id, brand_id) values('로즈 오브 노 맨스 랜드', 360000, '무인지대에 핀 장미라는 의미의 로즈 오브 노 맨즈 랜드는 1차 세계대전 당시 수천 명의 생명을 구한 간호사들에게 군인들이 붙인 애칭입니다. 그들의 이타심과 헌신에 대한 찬사를 담은 향기로 터키쉬 로즈페탈과 핑크페퍼콘의 부드러우면서 스파클링한 느낌으로 시작하여 가장 시크한 장미향을 구현합니다.', 90, 5, 3);
insert into products(name, price, description, stock, scent_id, brand_id) values('모하비 고스트', 360000, '모하비 사막의 건조하고 황량함을 이겨내고 피어난 "고스트 플라워(GI IOST FLOWER)"의 매혹적인 생존과 강인한 생명력에 영감을 받은 우디 향수입니다. 머스키한 암브레트의 가볍고 우아한 캐릭터 탑 노트와 신선한 자메이카 네스베리가 결합되어 있습니다. 파우더리한 바이올렛이 샌달우드를 드러내기 위해 펼쳐집니다. 마침내 따뜻한 머스크는 크리스피한 앰버와 시더 우드로 모하비 고스트의 영혼을 피부에 남깁니다.', 90, 2, 3);
insert into products(name, price, description, stock, scent_id, brand_id) values('믹스드 이모션', 360000, '우리 시대의 격동적인 본성을 반영하기 위해 고안된 서사시. 믹스드 이모션은 조각난 기억과 추상적인 생각을 향기로 번역하는 바이레도 철학을 바탕으로 현시대 사람들이 공유하는 마음의 상태를 향기로 스케치했습니다. 마테의 편안함과 카시스의 날카로운 달콤함이 우드 향조와 어우러지고 홍차의 편안한 향기는 바이올렛 리프와 만나 대조적인 화합을 만들어냅니다.', 90, 1, 3);
insert into products(name, price, description, stock, scent_id, brand_id) values('발 다프리크', 360000, '벤 고햄은 아버지의 일기를 읽으며 아프리카의 다양한 사람들을 만나고 여행하며 마치 아프리카에 도착해 있는 듯한 환상을 느꼈습니다. 아프리카의 무수한 문화, 춤, 예술, 음악. 그리고 상상 속 이벤트에 대한 아이디어를 기리기 위해 만든 향기로 우디 베이스의 생기로운 플로럴 향기입니다.', 90, 1, 3);
insert into products(name, price, description, stock, scent_id, brand_id) values('블랑쉬', 360000, '봄의 부활을 상징하는 튤립에서 영감을 받은 향으로 한 계절에 처음 맺는 꽃 봉오리처럼 활기넘치고 매력적이고 낙천적인 느낌을 담은 향기입니다. 신선한 숲 향의 탑 노트로 시작되어 갓 체취된 꽃들의 줄기를 연상케합니다. 하트 노트인 튤립은 탑 노트를 관통하여 살짝의 상쾌함을 더하고, 베티버는 숲의 향을, 그리고 블론드 우드는 부드러운 우디향을 더합니다.', 90, 2, 3);
insert into products(name, price, description, stock, scent_id, brand_id) values('영로즈 ', 360000, '영 로즈는 새로운 세대의 시각으로 전통을 재구성하며, 청춘의 영원한 불안함에 대한 찬사를 전합니다. 이 향수는 자신들만의 미래를 써내려가는 사람들의 후각적인 일기입니다. 짙은 다마스크 로즈 향기 위로 스파이시한 시추안 페퍼를 더해 고전적인 로맨스에 대한 새로운 해석을 제시하며 관습에 묶이지않는 독특한 매력을 느낄 수 있습니다.', 90, 2, 3);
insert into products(name, price, description, stock, scent_id, brand_id) values('집시 워터', 360000, '집시워터는 로마 문화의 아름다움, 독특한 관습, 친밀한 믿음, 그리고 차별화된 삶의 방식에 대한 찬가입니다. 이 향기는 타고난 유목민이 구축한 다채로운 생활 방식에 대한 꿈에서 일깨웁니다. 강렬한 앰버와 신선한 시트러스, 그리고 솔잎과 샌달우드는 늪에서 보내는 집시의 밤에 열기를 더해줍니다.', 90, 1, 3);
insert into products(name, price, description, stock, scent_id, brand_id) values('라임 바질 앤 만다린 코롱', 220000, '조 말론 런던의 시그니처 향. 카리브해의 산들바람에서 실려온 듯한 라임향에 톡 쏘는 바질과 향기로운 백리향이 더해져 독특한 조합을 만들어 냅니다. 현대적인 감각의 클래식한 향입니다.', 90, 2, 1);
insert into products(name, price, description, stock, scent_id, brand_id) values('바질 앤 네롤리 코롱', 220000, '런던의 모습을 그대로 담은 장난스럽고도 사랑스러운 향. 플로럴 향이 풍부한 네롤리에 바질의 기분 좋은 향이 더해져 즐거운 모험이 가득한 영국만의 독특한 향을 냅니다.', 90, 1, 1);
insert into products(name, price, description, stock, scent_id, brand_id) values('오렌지 블로썸 코롱', 220000, '드럽게 빛나는 정원 속 오아시스. 흰 붓꽃과 발사믹 베티버의 따뜻한 향기가 오렌지 꽃의 향과 함께 퍼지며 즐거움을 줍니다.', 90, 5, 1);
insert into products(name, price, description, stock, scent_id, brand_id) values('가드니아 앤 오드 앱솔뤼', 475000, '새하얗게 빛나며 아라비아 사막 깊은 곳까지 생기를 불어넣는 가드니아. 따스한 플로랄 향이 관능적인 오드, 따뜻한 앰버와 어우러져 반전 있는 매력을 선사합니다. 부드러운 화이트 머스크 향으로 마무리되어 매혹적인 느낌을 더합니다.', 90, 5, 1);
insert into products(name, price, description, stock, scent_id, brand_id) values('피오니 앤 블러쉬 스웨이드 코롱', 220000, '꽃의 귀족, 작약의 화려함을 담은 향. 화려하게 핀 작약에 더해진 붉은 사과의 향기로운 과즙과 순수한 자스민, 장미 그리고 카네이션, 블러쉬 스웨이드의 부드러운 관능미가 여운을 남깁니다.', 90, 2, 1);
insert into products(name, price, description, stock, scent_id, brand_id) values('블루 아가바 앤 카카오 코롱', 220000, '라틴음악의 리듬. 블루 아가바의 하트 노트위에 상큼한 라임향과 강렬한 자몽향이 춤을 추는 듯합니다. 바다 소금이 자연 그대로의 생 카카오에 스며들어 베티버, 시나몬, 머스크의 관능미를 발산시켜 줍니다.', 90, 1, 1);
insert into products(name, price, description, stock, scent_id, brand_id) values('실버 버치 앤 라벤더 코롱', 220000, '따스한 아침 햇살 아래 녹음이 무성한 정원. 그 아름다운 장면을 실버 버치(백자작나무)가 한 폭의 그림처럼 담아냅니다. 시원하고 상쾌한 공기가 잉글리쉬 라벤더 향 사이를 스쳐갑니다. 갓 짜낸 자몽 과즙이 상쾌함을 더해주고, 땅의 우디함을 담은 카리스마 있는 실버 버치와 강렬한 로스티드 오크가 향을 감싸안아 줍니다.', 90, 1, 1);
insert into products(name, price, description, stock, scent_id, brand_id) values('잉글리쉬 오크 앤 헤이즐넛 코롱', 220000, '잉글리쉬 오크 앤 헤이즐넛은 삼림지대에서 자라난 헤이즐넛의 바삭거림, 그레이프프루트의 매력, 상쾌한 이끼의 느낌, 엘리미의 스파이스, 우디한 베티버를 충분히 로스팅한 오크에서 담아내어 흙내음과 녹색이 어우러진 고혹적인 향을 선사합니다.', 90, 2, 1);
insert into products(name, price, description, stock, scent_id, brand_id) values('너트맥 앤 진저 코롱', 220000, '조 말론 런던이 개발한 최초의 향수. 샌달우드와 삼나무의 향에 너트맥과 생동감있는 생강향이 더해져 독특하면서도 중독성있는 향수입니다.', 90, 2, 1);
insert into products(name, price, description, stock, scent_id, brand_id) values('바닐라 앤 아니스 코롱', 220000, '바닐라 향을 현대적으로 해석한 향. 섬세한 바닐라 오키드와 진한 카라멜 향이 만나 따뜻하게 감싸주는 느낌을 선사합니다.', 90, 1, 1);
insert into products(name, price, description, stock, scent_id, brand_id) values('우드 세이지 앤 씨 솔트 코롱', 220000, '바람부는 해안을 따라 걸으며 일상을 벗어나보세요. 하얗게 부서지는 파도, 소금기를 머금은 신선한 바다 공기. 험준한 절벽에서 느껴지는 투박한 자연의 향기와 세이지의 우디한 흙 내음이 어우러져 자유롭고 활기찬 에너지와 즐거움이 가득합니다.', 90, 2, 1);
insert into products(name, price, description, stock, scent_id, brand_id) values('포멜로 파라디', 223000, '아틀리에 코롱 이라는 브랜드를 한국에서 유명하게 만든 대표적인 주인공. 생자몽 특유의 상큼한 쌉싸름함이 농약 하 나도 안 친것 같은 굉장히 싱싱하고 역동적인 향기로 기분좋게 퍼지는 향기가 납니다. 생자몽의 순수한 향기라기 보다는 자연에서 그대로 갓 따온 자몽을 하얀 탄산이 올라오는 사이다에 섞어 시원하게 손님에게 내온 생 자몽 에이드 같은 느낌의 향기라고 보시면 될 것 같네요. 사람의 피부에 따라서 약간씩 발향되는 상큼함의 정도가 차이가 나며 전체적으로 블랙커런트 덕분에 쌉싸름한 질감도 있습니다.', 90, 3, 5);
insert into products(name, price, description, stock, scent_id, brand_id) values('클레망틴 캘리포니아', 223000, '아틀리에 코롱 매장에 갔을 때, 추천 받을 시 베스트로 권해주는 향수입니다. 굉장히 시원한 소다향기 같은 느낌의 만다린 향기가 퍼지는데, 바질과 스타 아니스가 섞여 시원하고 경쾌한 소다향 같은 질감을 가지고 있습니다. 예를 들면, 시중에서 유명한 페라리 라이트 에센스 향수에서났던 달콤한 소다 향기에 꽃 잎이 무성한 만다린과 바질 잎 등을 섞어 탄산 기포를 시원하게 올려서 마치 밀키스가 연상되는 느낌의 부드럽고 스파클링한 상큼달콤한 향기가 완성됩니다. 사계절로 쓰기 좋지만 무엇보다 호불호 거의 갈리지 않아 부담없이 쓸 수 있는 향수라 좋습니다.', 90, 3, 5);
insert into products(name, price, description, stock, scent_id, brand_id) values('퍼시픽 라임', 178000, '상큼한 라임, 부드러움과 달콤함을 더해주는 오렌지가 어우러진 싱그럽고 청량한 첫 향을 지나면 스피어민트와 유칼립투스가 향기로운 그린 노트를 선사합니다. 코코넛이 부드러우면서도 따뜻한 느낌의 우디, 앰버와 어우러지면서 퍼시픽 라임의 깨끗한 잔향이 완성됩니다.', 90, 3, 5);
insert into products(name, price, description, stock, scent_id, brand_id) values('베르가못 솔레일', 230000, 'Bergamot Soleil 라벤더의 향이 담긴 섬세한 베르가못은 감정이 가득한 아름다운 추억에 담겨 있습니다.  이탈리아의 베르가못, 스리랑카의 검은 후추, 지중해의 라벤더 아틀리에 쾰른 (Atelier Cologne)이 만든 베르가못 솔레 (Bergamot Soleil) 향은 베르가못과 라벤더의 꽃 노트가 독특하게 혼합되어 있습니다.  미묘한 검은 후추 맛이 Absolue Cologne에 약간 매운 맛을줍니다.  남성과 여성을 위한 이 꽃 향기는 해가 뜨는 일몰이나 가을 아침의 단맛을 떠올리게합니다.  잊을 수없는 향기를 선사합니다. ', 90, 3, 5);
