insert into products(name, price, description, stock, scent_id, brand_id) values('베티베르 파탈', 180000, '흙의 향을 품은 울창한 치유의 숲 속. 그 곳에서 속삭이는 신비로운 영혼의 유혹', 90, 1, 5);
insert into products(name, price, description, stock, scent_id, brand_id) values('울랑 앙피니', 290000, '차분하고 편안한 운다는 단청처럼 고요하고 따뜻한 향. 아름다운 풍경처럼 우아하고 섬세한 느낌', 90, 2, 5);
insert into products(name, price, description, stock, scent_id, brand_id) values('트레프드 퍼르', 240000, '"행운이라는 건 느낄 수도 있지만, 느끼지 못하고 지나가기도 한다. 하지만 오늘은 예감이 좋았다. 느낌이 왔다. 시원한 부슬비가 내린 뒤 아름다운 햇빛이 모습을 드러낸 아침, 온 세상은 더 맑고 싱그러워 보였다. 정말 봄이 온 것이다."', 90, 2, 5);
