insert into products(name, price, description, stock, scent_id, brand_id) values('만다리노 디 아말피 오 드 퍼퓸', 275000, '구름 한 점 없는 산타모니카 해변, 달콤한 캘리포니아 오렌지와 DJ의 신나는 음악을 들으며 모든 일상의 고민을 날리는, 청량한 여름', 90, 3, 2);
