insert into products(name, price, description, stock, scent_id, brand_id) values('베네시안 베르가못 오 드 퍼퓸', 298000, '어둠이 짙게 내린 도시의 야경, 럭셔리한 건물의 조명 사이로 느리게 흐르는 유람선과 나긋한 대화소리가 들리는 이곳', 90, 1, 2);
insert into products(name, price, description, stock, scent_id, brand_id) values('솔레이네쥬 오 드 퍼퓸', 290000, '밤 사이 내린 진눈깨비, 얼어붙은 아스팔트 도시 위로 피어난 얼음꽃 한 송이, 꽃 위로 조용히 내려앉은 눈송이의 포근함', 90, 2, 2);
insert into products(name, price, description, stock, scent_id, brand_id) values('그레이 베티버 오 드 퍼퓸', 230000, '흙이 섞인 비바람을 그대로 맞고 있는 굵직한 레몬을 통으로 썰어낸 듯 거침없음과 산뜻함이 어울린 베티버 비누 향', 90, 1, 2);
