insert into products(name, price, description, stock, scent_id, brand_id) values('비터 피치 오 드 퍼퓸', 363000, '복숭아가 리드하는 한 편의 황홀경. 선악과를 탐닉하듯, 원초적인 본능의 자극', 90, 5, 2);
insert into products(name, price, description, stock, scent_id, brand_id) values('블랙 오키드 오 드 퍼퓸', 160000, '다크초콜릿과 달콤한 꽃을 양초로 그을려 어두운 칵테일바에 그려 넣은 듯한 향기', 90, 5, 2);
insert into products(name, price, description, stock, scent_id, brand_id) values('코스타 아주라 오 드 퍼퓸', 185000, '석양지는 일몰의 시간, 지중해의 신선한 바닷바람과 파도가 부서져 내리다', 90, 1, 2);
insert into products(name, price, description, stock, scent_id, brand_id) values('플레르 드 뽀', 255550, '플레드 드 뽀는 불어로 ‘피부의 꽃’이라는 뜻입니다. 고대 그리스 신화 중 가장 정열적이고 관능적인 프시케와 에로스의 사랑 이야기를 자연스러운 살 내음을 연상시키는 향으로 표현하였습니다.', 90, 2, 4);
insert into products(name, price, description, stock, scent_id, brand_id) values('오르페옹', 255550, '오르페옹(orphéon) 실내에 피어오르던 타바코 연기, 오래된 목재 장식에서 새어나오는 편안한 향, 바(bar)의 주인공인 남녀 고객의 분가루 향내가 교차되는 순간의 이미지를 연상시킵니다. 통카콩의 따스함이 백향목(시더우드)의 깊은 향, 그리고 주니퍼 베리의 선명함이 잊을 수 없는 이곳, 오르페옹(orphéon) 의 분위기를 다시 떠올려 줍니다.', 90, 1, 4);
insert into products(name, price, description, stock, scent_id, brand_id) values('도손', 255550, '수선화 베이스에 오렌지 꽃잎과 핑크 페퍼콘, 머스크 향이 더해진 부드러운 플로럴 계열의 오 드 뚜왈렛으로 수 많은 꽃송이가 부드러운 바람을 타고 전해지듯 고혹적인 향수입니다. 끝없이 펼쳐진 꽃밭, 그 너머에서 전해지는 화이트 머스크와 아이리스의 섬세한 파우더 향은 영원히 잊혀지지 않는 어린시절을 추억하게 만듭니다.', 90, 5, 4);
insert into products(name, price, description, stock, scent_id, brand_id) values('탐다오', 255550, '샌달 우드는 감각적이고 매력적인 향 때문에, 가장 수요가 많은 원료 중 하나입니다. 딥티크의 창시자인 크리스티앙 고트로와 이브 쿠에랑은 이 샌달우드를 사용하여 우디하면서도 동양 적인 향의 탐다오를 탄생시켰습니다. 탐다오는 사원과 숲, 그리고 통킨(Tonkin) 지역의 높은 곳에 있는 안개를 연상케 합니다.', 90, 1, 4);
insert into products(name, price, description, stock, scent_id, brand_id) values('필로시코스', 255550, '그리스 펠리온 산의 추억을 그린 향으로, 신선하고 달콤한 무화과 나무의 잎, 꽃, 수액까지 모두 느낄 수 있습니다.', 90, 1, 4);
insert into products(name, price, description, stock, scent_id, brand_id) values('오 로즈', 255550, '오드퍼퓸 오 로즈는 장미 향이 본래 내포하장미에 대한 또 다른 해석으로, 한층 더 풍부하고 생동감 넘치는 장미의 특성을 보여줍니다. 캐모마일과, 아티초크, 그리고 리치의 조합을 통해 누구도 예상하지 못한 향 조합을 핵심 노트로 표현합니다.', 90, 5, 4);
insert into products(name, price, description, stock, scent_id, brand_id) values('뗌 포', 255550, '1960년대를 풍미한 후각적 아이콘인 파출리에 대한 예찬으로 탄생한 작품입니다. 신령과 짐승들이 살고 있는 파출리가 가득핀 숲을 강렬한 색상과 풍부한 무늬의 일러스트로 표현하고 있습니다.', 90, 1, 4);
insert into products(name, price, description, stock, scent_id, brand_id) values('로 파피에', 255550, 'Diptyque가 홀리데이 시즌을 맞아 불꽃을 독창적인 방법으로 찬미합니다. ‘계몽주의 시대’의 최초의 백과사전에 실린 « 불꽃 »에 대한 시적 정의를 구성하는 단어들로 형성된 서체를 통해 이 불꽃은 특별 컬렉션의 핵심적이고 주된 장식 패턴이 되었습니다.', 90, 2, 4);
